<script>
// Tab functionality
function openTab(evt, tabName) {
    var i, tabPanes, tabButtons;
    tabPanes = document.getElementsByClassName("about-tab-pane");
    for (i = 0; i < tabPanes.length; i++) {
        tabPanes[i].classList.remove("active");
    }
    tabButtons = document.getElementsByClassName("about-tab-btn");
    for (i = 0; i < tabButtons.length; i++) {
        tabButtons[i].classList.remove("active");
    }
    document.getElementById(tabName).classList.add("active");
    evt.currentTarget.classList.add("active");
}

// Portfolio tabs
$(document).ready(function() {
    $('.tab-link').on('click', function(e) {
        e.preventDefault();
        var targetTab = $(this).data('tab');
        $('.tab-link').removeClass('active');
        $('.tab-pane').removeClass('active');
        $(this).addClass('active');
        $('#' + targetTab).addClass('active');
    });
    
    // Load More
    $('.load-more-btn').on('click', function() {
        var tabId = $(this).data('tab');
        var hiddenItems = $('#' + tabId + ' .port-img.hidden');
        if (hiddenItems.length > 0) {
            hiddenItems.slice(0, 6).removeClass('hidden');
            if ($('#' + tabId + ' .port-img.hidden').length === 0) {
                $(this).hide();
            }
        }
    });
    
    // FAQ Accordion
    $('.faq-question').on('click', function() {
        $(this).toggleClass('active');
        $(this).next('.faq-answer').toggleClass('active').slideToggle(300);
    });
    
    // Form submissions
    $('#heroForm').on('submit', function(e) {
        e.preventDefault();
        var formData = $(this).serialize();
        $.ajax({
            url: 'send_email.php',
            type: 'POST',
            data: formData,
            success: function(response) {
                $('#heroFormMessage').html('<div class="alert alert-success">Thank you!</div>');
                $('#heroForm')[0].reset();
            }
        });
    });
    
    $('#storyForm').on('submit', function(e) {
        e.preventDefault();
        var formData = $(this).serialize();
        $.ajax({
            url: 'send_email.php',
            type: 'POST',
            data: formData,
            success: function(response) {
                $('#storyFormMessage').html('<div class="alert alert-success">Message sent!</div>');
                $('#storyForm')[0].reset();
            }
        });
    });
});

// Helper functions
function getStarted() {
    $('html, body').animate({
        scrollTop: $('.hero-form').offset().top - 100
    }, 500);
}

function toggleChat() {
    if (window.LiveChatWidget) {
        window.LiveChatWidget.call('maximize');
    }
}
</script>
    
