<script>
  // Testimonial Slider
   // Global variables
    let currentIndex = 0;
    let autoSlideInterval;
    const btns = document.querySelectorAll(".testimonial-main-section .btn");
    const slideRow = document.getElementById("slide-row");
    const mainWrapper = document.querySelector(".testimonial-main-section .testimonial-wrapper");
    const section = document.querySelector(".testimonial-main-section");
    const videos = document.querySelectorAll("video");
    
    // Toggle Mute/Unmute Function
    function toggleMute(videoId, button) {
        const video = document.getElementById(videoId);
        const icon = button.querySelector('i');
        
        if (video.muted) {
            video.muted = false;
            icon.className = 'fas fa-volume-high';
        } else {
            video.muted = true;
            icon.className = 'fas fa-volume-xmark';
        }
    }
    
    // Update Slide Function
    function updateSlide() {
        if (!mainWrapper || !slideRow) return;
        const mainWidth = mainWrapper.offsetWidth;
        const translateValue = currentIndex * -mainWidth;
        slideRow.style.transform = `translateX(${translateValue}px)`;
        btns.forEach((btn, index) => {
            btn.classList.toggle("active", index === currentIndex);
        });
    }
    
    // Next Slide Function
    function nextSlide() {
        currentIndex = (currentIndex + 1) % 2;
        updateSlide();
    }
    
    // Previous Slide Function
    function previousSlide() {
        currentIndex = (currentIndex - 1 + 2) % 2;
        updateSlide();
        stopAutoSlide();
        startAutoSlide();
    }
    
    // Next Slide Manual Function
    function nextSlideManual() {
        nextSlide();
        stopAutoSlide();
        startAutoSlide();
    }
    
    // Start Auto Slide
    function startAutoSlide() {
        stopAutoSlide();
        autoSlideInterval = setInterval(nextSlide, 5000);
    }
    
    // Stop Auto Slide
    function stopAutoSlide() {
        if (autoSlideInterval) {
            clearInterval(autoSlideInterval);
        }
    }
    
    // Pause all videos
    function pauseAllVideos() {
        videos.forEach(video => {
            video.pause();
        });
    }
    
    // Play all videos
    function playAllVideos() {
        videos.forEach(video => {
            video.play();
        });
    }
    
    // Initialize
    btns.forEach((btn, index) => {
        btn.addEventListener("click", () => {
            currentIndex = index;
            updateSlide();
            stopAutoSlide();
            startAutoSlide();
        });
    });
    
    // Handle hover - pause videos and stop auto-slide
    section.addEventListener("mouseenter", () => {
        pauseAllVideos();
        stopAutoSlide();
    });
    
    section.addEventListener("mouseleave", () => {
        playAllVideos();
        startAutoSlide();
    });
    
    window.addEventListener("resize", () => {
        updateSlide();
    });
    
    // Start auto slide on page load
    startAutoSlide();
</script>
